(1, 2, '2022-02-01', '001/2022', '001/2022', 'PARA QUE SEJA FEITO UM VEEMENTE APELO AO EXCELENTÍSSIMO PREFEITO DO MUNICÍPIO, DATA VÊNIA (EXPRESSÃO LATINA QUE SIGNIFICA "DADA A LICENÇA" OU DADA A PERMISSÃO) EXTENSIVO PRINCIPALMENTE A SECRETÁRIA DE DESENVOLVIMENTO URBANO E MEIO RURAL,PARA QUE SEJA EFETUADO OS SERVIÇOS DE SANEAMENTO PARA ABSORVE AS ÁGUAS QUE VEM DO BAIRRO NOVA ALIANÇA', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/202301184712.pdf', 'Vereador', 15, 'N', '', ''),
(2, 2, '2022-02-01', '001/2022', 'N°001/2022 - JAIRO GUILHERME DA SILVA', 'PARA QUE SEJA FEITO UM VEEMENTE APELO AO EXCELENTÍSSIMO PREFEITO DO MUNICÍPIO, DATA VÊNIA (EXPRESSÃO LATINA QUE SIGNIFICA "DADA A LICENÇA" OU DADA A PERMISSÃO) EXTENSIVO PRINCIPALMENTE A SECRETÁRIA DE DESENVOLVIMENTO URBANO E MEIO RURAL,PARA QUE SEJA EFETUADO OS SERVIÇOS DE SANEAMENTO PARA ABSORVE AS ÁGUAS QUE VEM DO BAIRRO NOVA ALIANÇA', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/202301184725.pdf', '0', 15, 'S', '', ''),
