INSERT INTO `vereador` (`id`, `nome`, `partido`, `email`, `historico`, `foto`, `legislatura`, `whatsapp`, `instagram`, `facebook`, `ativo`) VALUES
(12, 'JAIME CALDAS DA SILVA JUNIOR', 'PSB', '', '<p class="MsoNormal" style="text-align:justify"><b><span style="font-size:16.0pt;line-height:115%">JAIME CALDAS DA SILVA\r\nJUNIOR<o:p></o:p></span></b></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de Palmeirina-PE,  cursou o 2º Grau Completo na Escola Azarias\r\nSalgado Angelim PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">JAIME CALDAS DA SILVA JUNIOR é um político que sempre se\r\ndedicou a sua luta para o homem do campo. Ele nasceu em uma família de\r\nagricultores em Palmeirina, Pernambuco, e desde cedo aprendeu a valorizar o\r\ntrabalho e a fé no campo.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi reeleito pela terceira oportunidade, vereador de Angelim nas\r\neleições Municipais de 2020, pelo PSB, tendo conquistado 308 votos. Antes havia\r\nvencido as eleições de 2016 pelo PSD com 315 votos e em 2012 eleito pelo PSD\r\ncom 428 votos.  Tendo como Bandeira: A\r\nluta pelo apoio ao homem do campo.<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Atualmente exerce a função de relator da Comissão de Obras e\r\nServiços Públicos, Urbanismo, Agricultura e meio ambiente. Eleito para o Biênio\r\nde 2023 e 2024.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">CONTATO: 87981699082<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Sitio Buraco D’água Nº   CEP: 55.430.00 Angelim PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"> </span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.JAIME-CALDAS-DA-SILVA-JUNIOR.png', '2021-2024', '87981699082', '', '', 'S'),
(13, 'CLAUDECI MARIA FERREIRA DA SILVA', 'DEM', 'mclaudecir10@Gmail.com', '<p class="MsoNormal" style="text-align:justify"><b><span style="font-size:16.0pt;line-height:115%">CLAUDECI MARIA FERREIRA\r\nDA SILVA<o:p></o:p></span></b></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de Angelim-PE conclui-o a 7ª série do Primeiro Grau\r\nna Escola Estadual Azarias Salgado Angelim PE <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">CLAUDECI MARIA FERREIRA DA SILVA representa a força feminina\r\nno Campo, é uma mulher agricultura que trabalha com amor sempre focado nas\r\natividades do campo. Ela começou  suas\r\natividades no campo há vários anos. Desde então, ela se dedica a cuidar da\r\nterra, das plantas e dos animais, com a ajuda de seus filhos.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi eleita, vereadora de Angelim nas eleições Municipais de\r\n2020, pelo DEM, tendo conquistado 343 votos. Tendo como Bandeira: Se dedicado\r\naos trabalhos relacionados aos agricultores e acompanhar e fiscalizar os\r\ntrabalhos relacionados a infraestrutura do município sempre focando no bem\r\nestar da população.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">CONTATO: 87998094710<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Rua Presidente Tancredo Neves Nº 30  Centro CEP: 55.430.00 Angelim PE<o:p></o:p></span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.CLAUDECI-MARIA-FERREIRA-DA-SILVA.png', '2021-2024', '87996072505', '', '', 'S'),
(14, 'ALEXANDRO FERREIRA DA ROCHA', 'PT', 'sandroferreirarocha67@Gmail.com', '<p class="MsoNormal" style="text-align:justify"><b><span style="font-size:16.0pt;line-height:115%">ALEXANDRO FERREIRA DA\r\nROCHA<o:p></o:p></span></b></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de Angelim-PE Cursando Administração pela UNIP-\r\nGaranhuns -PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Exerceu a função de Motorista pela Prefeitura Municipal de\r\nAngelim PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi reeleito pela Segunda oportunidade, vereador de Angelim\r\nnas eleições Municipais de 2020, pelo PT, tendo conquistado 346 votos. Antes havia\r\nvencido as eleições de 2016 pelo PSDB com 526 votos. Defensor dos direitos trabalhistas\r\ne infraestrutura.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">CONTATO: 87991563821<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Endereço: Travessa Sergio Loreto Nº 55 Centro CEP: 55.430.00\r\nAngelim-PE<o:p></o:p></span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.ALEXANDRO-FERREIRA-DA-ROCHA.png', '2021-2024', '87991563821', '', '', 'S'),
(15, 'JAIRO GUILHERME DA SILVA', 'DEM', 'E-mail: guilhermejairo6507@Gmail.com', '<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"><br></span></p><p class="MsoNormal" style="text-align:justify"><b style="font-size: 1rem;"><span style="font-size:16.0pt;line-height:115%"><br></span></b></p><p class="MsoNormal" style="text-align:justify"><b style="font-size: 1rem;"><span style="font-size:16.0pt;line-height:115%">JAIRO GUILHERME DA\r\nSILVA</span></b><br></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"> </span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de Angelim-PE há 52 anos, Jairo Felipe é aposentado\r\nda Policia Militar, fundamenta seu mandato pelos ideias na qualidade do\r\ntrabalho digno e de justiça social.<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Exerceu a função de Policial Militar, em Varias cidades do agreste\r\nMeridional de Pernambuco: Palmeirina, canhotinho, Angelim, Calçados, Lajedo,\r\nJupi, Jucati, São João, Garanhuns, Iati, Brejão, e Bom Conselho, tendo se\r\ndestacado no cargo pelo espirito publico e a sensibilidade, sobretudo junto aos\r\nmais necessitados. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi eleito Vereador de Angelim nas Eleições Municipais de\r\n2020, pelo DEM, tendo conquistado 352 votos. Atuando sempre pelos mais\r\nnecessitados.<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Atualmente exerce a função de membro da Comissão de  Finanças e Orçamento e Presidente da Comissão\r\nde Segurança e Cidadã. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">ENDEREÇO: Loteamento Madalena Sofia Nº 17 CEP 55.430.000\r\nANGELIM PE<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"> </span></p><p class="MsoNormal" style="text-align:justify">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class="MsoNormal" style="text-align:justify"><br></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.JAIRO-GUILHERME-DA-SILVA.png', '2021-2024', '87981358125', 'https://www.instagram.com/jairofilipi/', 'https://www.instagram.com/jairofilipi/', 'S'),
(16, 'SEVERINO JOSE DE OLIVEIRA', 'PT', 'vereadoroliveira2012@Hotmail.com', '<p class="MsoNormal" style="text-align:justify"><b><span style="font-size:16.0pt;line-height:115%">SEVERINO JOSÉ DE\r\nOLIVEIRA<o:p></o:p></span></b></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de Angelim-PE formado em licenciatura em Geografia\r\npela Universidade de Pernambuco (UPE) CAMPUS Garanhuns.<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi agricultor (Homem do Campo), exerceu a função de Militar\r\ndo (EB) Exercito Brasileiro, Soldado de 1994 á 2000, trabalhou na Loja Armazém\r\nDourado na Cidade de Garanhuns-PE, trabalhou como: Palestrante na empresa\r\nCELPE, como voluntario. Exerceu a Função de Diretor na Unidade Mista Santa\r\nTerezinha na Cidade de Angelim-PE<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi reeleito pela terceira oportunidade, vereador de Angelim\r\nnas eleições Municipais de 2020, pelo PT, tendo conquistado 420 votos. Antes\r\nhavia vencido as eleições de 2008 pelo PT com 483 votos e em 2016 eleito pelo\r\nPT com 431 votos.  Tendo como Bandeira:\r\nEducação e Cultura, defensor dos direitos trabalhistas e politicas púbicas.<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size: 16pt;">Atualmente exerce a função de Presidente da Comissão de Finanças\r\ne Orçamento. Relator das Comissões de: Legislação, Justiça e Redação; Membro\r\ndas Comissões de: Educação, Saúde, Cultura, Turismo, Deporto, e Assistência\r\nSocial; Obras e serviços Públicos, Urbanismo, Agricultura e Meio Ambiente e\r\nSegurança Cidadã.</span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"><o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">CONTATO: 87996120885<o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify">\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Rua Alex Alessandro Nº 64 centro CEP: 55.430.00 Angelim PE<o:p></o:p></span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.SEVERINO-JOSE-DE-OLIVEIRA.png', '2021-2024', '87996120885', '', '', 'S'),
(17, 'HERÁCLITO LUPÉRCIO LOPES DE SANTANA', 'DEM', 'lupercio.santana@hotmail.com', '<p class="MsoNormal" style="text-align:justify"><b><span style="font-size: 16pt; line-height: 115%;"><span style="color: #000000;">HERACLITO\r\nLUPERCIO LOPES DE SANTANA</span><span style="color: red;"><o:p></o:p></span></span></b></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">Natural de Canhotinho-PE formado em Administração pela\r\nFaculdade de Administração de Garanhuns (FAGA). Esta cursando Direito pela\r\nFaculdade de Direito (FDG) de Garanhuns PE <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">Exerceu a função de Estagiário na Caixa Econômica Federal\r\n(Posto de Atendimento Angelim-PE), Trabalhou na Prefeitura Municipal de Angelim,\r\nFunção: Auxiliar Administrativo e Motorista. Trabalhou no Centro Hospitalar São\r\nMarcos Recife-PE Função: Encarregado de estoque. Ingressou através de Concurso Público\r\nno DETRAN-PE, exercendo a função de: Agente de Transito e Escrivão da Policia\r\nCivil de PE. Este ultimo, também através de concurso Público.  <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">Foi eleito, vereador de Angelim nas eleições Municipais de\r\n2020, pelo DEM, tendo conquistado 426 votos. Tendo como Bandeira: Ética e\r\nCompromisso com os mas necessitados.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">Adotando uma postura política de independência, vem se\r\nnotabilizando por exercer um mandato equilibrado, que reconhece as ações Governistas,\r\nmas que cobra a efetiva solução de problemas e pontua denúncias acerca de\r\npossíveis irregularidades, sempre em benefício do cidadão angelinense.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">Atualmente exerce as funções de: 1º Secretário da Casa pelo\r\nDEM, presidente das Comissões Permanente de: Legislação, Justiça e Redação, e Comissões\r\nPermanente de Obras e Serviços Público, e meio ambiente.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%"> </span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">CONTATO: 87998094710<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%">Rua São José Nº 253 centro CEP: 55.430.00 Angelim PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:12.0pt;\r\nline-height:115%"> </span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.HERACLITO-LUPERCIO-LOPES-DE-SANTANA.png', '2021-2024', '87998094710', '', '', 'S'),
(18, 'MAURILIO EDSON CAVALCANTI DE VASCONCELOS', 'PSB', 'mauriliome@icloud.com', '<p class="MsoNormal" style="text-align:justify"><b><span style="font-size: 16pt; line-height: 115%;"><span style="color: #000000;">MAURILIO\r\nEDSON CAVALCANTI DE VASCONCELOS</span><span style="color: red;"><o:p></o:p></span></span></b></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de Angelim-PE formado em técnico em agropecuária,\r\npela Fundação Bradesco Garanhuns-PE.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Exerceu a função de: Padeiro na Panificadora SUPAPO. Trabalhou\r\nno Mercadinho Duarte como: embalador e repositor. Trabalhou na Fabrica de Milho\r\nMavam: como Operador de Maquina. Trabalhou na Prefeitura Municipal de Angelim\r\ncomo Motorista e Diretor de transporte.  <o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Foi reeleito pela Quarta oportunidade, vereador de Angelim\r\nnas eleições Municipais de 2020, pelo PSB, tendo conquistado 437 votos.\r\nAntes havia vencido as eleições de 2016 pelo PSB com 326 votos, em 2012 eleito\r\npelo PSB com 329 votos e em 2008 pelo PSB com 369 votos. Tendo como Bandeira: A Saúde. <o:p></o:p></span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"><span style="font-size:16.0pt;line-height:115%;\r\nfont-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;mso-ascii-theme-font:minor-latin;mso-fareast-font-family:\r\nCalibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:minor-latin;\r\nmso-bidi-font-family:&quot;Times New Roman&quot;;mso-bidi-theme-font:minor-bidi;\r\nmso-ansi-language:PT-BR;mso-fareast-language:EN-US;mso-bidi-language:AR-SA">Atualmente\r\nexerce a função de membro da Comissão de: \r\nLegislação, Justiça e Redação e Relator das Comissões de: Finanças e\r\nOrçamento; Educação, Saúde, Cultura, Turismo, Deporto e Assistência Social; e\r\nSegurança Cidadâ. Para o Biênio de 2023 a 2024.</span><br></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size: 16pt;">CONTATO: 87996241610</span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Rua Alexandre Pereira Filho Nº 26, Centro CEP:55.430.00\r\nAngelim PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><br></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"> <br></span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.MAURILIO-EDSON-CAVALCANTI-DE-VASCONCELOS.png', '2021-2024', '87996241610', '', '', 'S'),
(19, 'BRUNO DOS SANTOS CALDAS', 'PSB', 'brunosantos2203@gmail.com', '<p class="MsoNormal" style="text-align:justify"><b style="font-size: 1rem;"><span style="font-size:16.0pt;line-height:115%">BRUNO DOS SANTOS CALDAS</span></b><br></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Natural de São Paulo-SP, pai de dois filhos (Vinícius e Davi),\r\nCasado Com: Daise Mirele. Fez Segundo Grau Completo em Recife na Escola Barão do\r\nBonito.<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size: 16pt;">Exerceu a função de Balconista e Gerente na Panificadora\r\nSolimões em Recife, pelo período de sete anos. Trabalhou na Loja Art-Estofados,\r\nno Período de três anos no Bairro da Madalena em Recife, na função de Vendedor\r\ne trabalhou de encarregado na Empresa Milharal em Angelim por 4 anos.</span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size: 16pt;">Foi reeleito pela segunda oportunidade, vereador de Angelim\r\nnas eleições Municipais de 2020, pelo PSB, tendo conquistado 472 votos. Antes havia\r\nvencido as eleições de 2016 pelo PSB com 256 votos. Tendo como Bandeira:\r\nEducação, Cultura, Esporte, Saúde e Assistência Social. Defensor dos direitos\r\nhumanos, trabalhistas e politicas púbicas Sociais.</span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size: 16pt;">Atualmente é o presidente da Câmara de Vereadores de Angelim,\r\nfunção ao qual foi eleito para o biênio 2023/2024.</span></p><p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"><o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">CONTATO: 81999201268<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%">Rua Mario Melo Nº 152 centro CEP: 55.430.00 Angelim PE<o:p></o:p></span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"> </span></p>\r\n\r\n<p class="MsoNormal" style="text-align:justify"><span style="font-size:16.0pt;\r\nline-height:115%"> </span></p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.BRUNO-DOS-SANTOS-CALDAS.png', '2021-2024', '81999201268', '', '', 'S'),
(20, 'NELSON PEREIRA DA SILVA', 'DEM', '', '<p class="MsoNormal" style="text-align:justify">NELSON PEREIRA DA SILVA</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">Natural de Angelim-PE Agricultor.  É homem do campo que vive e trabalha no Povoado de Quatro Bocas em Angelim PE.</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">Aprendeu a trabalhar na terra desde cedo, com seu pai, que era um pequeno agricultor. Ele sempre sonhou em ter sua própria terra. Desde então, ele se dedica a melhorar a qualidade de vida do povo angelinense.</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">Identificado com as causas rurais, possui uma extensa nota de serviços prestados aos agricultores e pecuaristas do Município, sobretudo de base familiar, mas também tem atuação de destaque no segmento de Saúde Pública.</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">Foi reeleito pela Sexta oportunidade, vereador de Angelim nas eleições Municipais de 2020, pelo DEM, tendo conquistado 000  votos. Antes havia vencido as eleições de: 2016 pelo P com 555 votos. Em 2012 eleito pelo P com 704 votos. Em 2008 eleito pelo P com 485 votos. Em 2004 eleito pelo P com 252 votos.  e em 1992 eleito pelo P com 217 votos.</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">Atualmente exerce a função de Segundo Secretario da Mesa Diretora e presidente da Comissão Permanente de Educação, Saúde, Cultura, Turismo, Deporto e Assistência Social.</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">CONTATO: 87998094710</p><p class="MsoNormal" style="text-align:justify"><br></p><p class="MsoNormal" style="text-align:justify">Povoado quatro Bocas Nº  CEP: 55.430.00 Angelim PE</p>', 'http://it-solucoes.inf.br/admnews/CM_ANGELIM/20230117.NELSON-PEREIRA-DA-SILVA.png', '2021-2024', '', '', '', 'S');
